import React from 'react';

const CartItem = ({item}) => {
  const{ id, title, image, price, amount} = item 
  return(
    <div>
      <div className='w-full min-h-[150px]'>
        <div>
          <img className='max-w-[80px]' src={image} alt='product image' />
        </div>
      </div>
    </div>
  );
};

export default CartItem;
